mapper.select:

		Select Rownum As rn, b.*
		FROM	(
		        Select b.*, u.nick, u.img as user_img, replyCount(b.brd_num) replyCount,
		                count_like(b.brd_num) as like_cnt
		                ,TOGGLE_BURNING_COUNT_FUNCTION2(b.brd_num) as report_cnt
		                , (SELECT 	COUNT(*)
		                   FROM   	board_like bl
		                   WHERE 	bl.brd_num = b.brd_num
		                   AND 		bl.user_num = #{b_user_num}
		                  ) AS likeyn
		                , (SELECT	SUM(cnt)
		                   FROM		report r
		                   WHERE	r.brd_num	= b.brd_num
		                   AND		r.user_num	= #{b_user_num}
		                   ) As myBurning
		                , Row_Number() Over (Order By brd_group Desc, brd_step) As row_num
		        From    board b
		                INNER JOIN  user1 u On  b.user_num = u.user_num
		        Where   brd_md	= 	100
                and     brd_lg	=	700
                and     chg_id	=	#{chg_id}

		        ) b
		Where b.row_num Between #{start} 
						And 	#{end}

mapper.update:
Update	board
Set		title = #{title}, 
		conts = #{conts}

			,img = #{img}

Where	brd_num = #{brd_num}

mapper.delete:
		Delete From	board


                Where	brd_group = #{brd_num}  


  				Where	brd_num = #{brd_num}	
	  			And    	brd_group = #{brd_group}

mapper.select:
{
	call Challenge_Comment_Insert_Proc(
				#{brd_num,		mode=IN,	jdbcType=INTEGER},
				#{chg_id,		mode=IN,	jdbcType=INTEGER},
				#{user_num,		mode=IN,	jdbcType=INTEGER},
				#{title,		mode=IN,	jdbcType=VARCHAR},
				#{conts,		mode=IN,	jdbcType=VARCHAR},
				#{resultCount,	mode=OUT,	jdbcType=INTEGER}
	)
}

Select * From (Select rownum rn, a.* From (Select b.*, u.nick, u.img as user_img, replyCount(b.brd_num) replyCount ,count_like(b.brd_num) as like_cnt ,toggle_burning_count_function2(b.brd_num) as report_cnt ,(SELECT COUNT(*) FROM board_like bl WHERE bl.brd_num = b.brd_num AND bl.user_num = 1 ) AS likeyn , (SELECT SUM(cnt) FROM report r WHERE r.brd_num = b.brd_num AND r.user_num = 1 ) As myBurning From board b Inner Join user1 u On b.user_num = u.user_num Where brd_md = 100 And brd_lg = 700 And chg_id = #{chg_id}
And b.title Like '%' || #{keyword} || '%'
And b.conts Like '%' || #{keyword} || '%'
And nick Like '%' || #{keyword} || '%'
Order by brd_group, brd_step
Order by brd_group Desc, brd_step
Order by brd_group Desc, brd_step
) a ) Where rn Between #{start} And #{end}

{
	? = call Toggle_Burning_Count_Function(
		 #{brd_num,		mode=IN,	jdbcType=INTEGER}
		,#{user_num,	mode=IN,	jdbcType=INTEGER}
	)
}
