mapper.select:
	SELECT  COUNT(Report_id)
    FROM    report
    WHERE   brd_num = #{brd_num} 
    AND		user_num = #{user_num}

	SELECT  cnt
    FROM    report
    WHERE   brd_num = #{brd_num} 
    AND		user_num = #{user_num}

	SELECT  cnt, COUNT(Report_id) As burningRecord
    FROM    report
    WHERE   brd_num = #{brd_num} 
    AND		user_num = #{user_num}

mapper.update:
	UPDATE	report
	SET		cnt = 0
	WHERE   brd_num = #{brd_num} 
    AND		user_num = #{user_num}

	UPDATE	report
	SET		cnt = 10
	WHERE   brd_num = #{brd_num} 
    AND		user_num = #{user_num}

mapper.select:
Select * From (Select rownum rn, a.* From (Select r.report_id, r.report_date, b.title, b.conts, b.img, b.reg_date, r.brd_num , r.cnt , u1.img As user_img , c.ctn , u1.user_name As reported_name , u1.user_id As reported_id , u1.nick As reported_nick , u2.user_name As reporter_name , u2.user_id As reporter_id , u2.nick As reporter_nick From report r, board b, comm c, user1 u1, user1 u2 Where r.brd_num = b.brd_num And b.brd_lg = 700 And b.brd_lg = c.lg And b.brd_md = c.md And b.user_num = u1.user_num And r.user_num = u2.user_num
And r.brd_num = #{keyword}
And u2.user_id = '${keyword}'
AND r.report_date BETWEEN TO_DATE(#{startDate}, 'YYYY-MM-DD') And TO_DATE(#{endDate}, 'YYYY-MM-DD') + INTERVAL '1' DAY
Order By r.report_id desc) a ) Where rn Between #{start} And #{end}

