mapper.update:
UPDATE board
SET like_cnt = like_cnt + 1
WHERE brd_num = #{brd_num}

UPDATE board SET title = #{title}, conts = #{conts},
img = #{img},
price = #{price}, bank_info = #{bank_info}, bank_duedate = #{bank_duedate}, addr = #{addr}, applicants = #{applicants}, user_tel = #{user_tel} WHERE brd_num = #{brd_num}

UPDATE board SET conts=#{conts}
WHERE  user_num = #{user_num} 
AND    brd_num =#{brd_num}

mapper.delete:
DELETE FROM board
WHERE user_num = #{user_num} 
AND brd_num = #{brd_num}
