mapper.update:
	    UPDATE user1
	    SET nick = #{nick},
	        user_name = #{user_name},
	        email = #{email},
	        birth = #{birth}, 
	        addr = #{addr},
	        tel = #{tel}

            , img = #{img}

	    WHERE user_id = #{user_id}

UPDATE user1
SET    img = #{img}
WHERE user_num = #{user_num}
