mapper.select:
SELECT COUNT(*) FROM challenge WHERE state_lg = 300 AND state_md = 102
AND chg_md = #{chg_md}
AND (title LIKE '%' || #{keyword} || '%' OR chg_conts LIKE '%' || #{keyword} || '%')

SELECT COUNT(*) FROM challenge WHERE state_lg = 300 AND state_md = 103
AND chg_md = #{chg_md}
AND (title LIKE '%' || #{keyword} || '%' OR chg_conts LIKE '%' || #{keyword} || '%')

SELECT chl.* , count_challenger(chl.chg_id) AS chlgerCnt , pickyn(chl.chg_id,#{my_user_num}) AS pickyn , count_pick(chl.chg_id) AS pick_cnt , sel_nick(chl.user_num) AS nick , sel_user_name(chl.user_num) AS userName , ctn_by_chg_id(chl.chg_lg,chg_md) AS ctn , sel_user_id(chl.user_num) AS userId FROM ( SELECT rownum rn, a.* FROM (SELECT * FROM challenge c WHERE c.state_lg = 300
AND c.state_md = 102 AND c.state_md = 102 AND c.state_md = 103
AND c.chg_md = #{chg_md}
AND (title LIKE '%' || #{keyword} || '%' OR chg_conts LIKE '%' || #{keyword} || '%')
ORDER BY c.create_date ORDER BY c.create_date ORDER BY count_pick(chg_id) ORDER BY ( SELECT COUNT(cr.chg_id) AS cnt FROM challenger cr WHERE cr.chg_id = c.chg_id GROUP BY cr.chg_id )
DESC) a ) chl WHERE rn BETWEEN #{start} AND #{end}
