mapper.update:
UPDATE board 
SET 

    img = #{img},

title = #{title},
conts = #{conts}
WHERE  brd_num = #{brd_num}

UPDATE board SET conts=#{conts}
WHERE  user_num = #{user_num} 
AND    brd_num =#{brd_num}

mapper.delete:
DELETE FROM board
WHERE user_num = #{user_num} 
AND brd_num = #{brd_num}

mapper.select:

SELECT *
FROM (
   		 SELECT ROWNUM AS r, a.*
   		 FROM (
		        SELECT b.brd_num, count_like(b.brd_num) as like_cnt, u.user_num, u.user_id, b.title, b.conts, u.nick, b.img, b.view_cnt, b.reg_date, b.price, b.bank_info, b.bank_duedate, b.addr, b.applicants, b.participants, b.user_tel, b.brd_group, b.brd_step, replyCount(b.brd_num) replyCount, b.zipCode
                 , likeyn(b.brd_num, #{b_user_num}) AS likeyn
		        FROM board b, user1 u
		        WHERE b.user_num = #{user_num} AND u.user_num = #{user_num} 
		        AND brd_lg = 700 AND brd_md = 102 AND brd_step = 0 
    ) a
)
WHERE r BETWEEN #{start} AND #{end}

SELECT *
FROM (
    SELECT ROWNUM AS r, b.*
    FROM (
        SELECT s.brd_num, b.title, b.bank_info, b.bank_duedate, b.addr, b.applicants, b.price, u.user_name
        FROM board b, sharingList s, user1 u
        WHERE s.user_num = #{user_num} 
        AND u.user_num = b.user_num
        AND b.brd_num = s.brd_num
        AND s.state_md = 101
    ) b
)
WHERE r BETWEEN #{start} AND #{end}

SELECT * FROM ( SELECT rownum r, b.* FROM ( SELECT b.brd_num, count_like(b.brd_num) as like_cnt, u.user_num, u.user_id, b.title, b.conts, u.nick, b.img, b.view_cnt, b.reg_date, b.price, b.bank_info, b.bank_duedate, b.addr, b.applicants, b.participants, b.user_tel, b.brd_group, b.brd_step, replyCount(b.brd_num) replyCount, b.zipCode, likeyn(b.brd_num, #{b_user_num}) AS likeyn
FROM board b, user1 u WHERE b.user_num = u.user_num AND b.brd_lg = 700 AND b.brd_md = 102 AND (title LIKE '%' || #{keyword} || '%') AND b.brd_step = 0 ORDER BY
b.view_cnt DESC
b.reg_date DESC
) b ) WHERE r BETWEEN #{start} AND #{end}
