mapper.select:
		SELECT *
		FROM (
		    SELECT ROWNUM AS r, a.*
		    FROM (
		        SELECT s.brd_num, b.title, s.state_md, s.reject_msg
		        FROM sharingList s
		        INNER JOIN board b ON s.brd_num = b.brd_num
		        WHERE s.user_num = #{user_num}
		        ORDER BY s.brd_num DESC
		    ) a
		)
WHERE r BETWEEN #{start} AND #{end}

mapper.update:
UPDATE sharingList
SET	   state_md =101
WHERE  brd_num = #{brd_num}
AND    user_num = #{user_num}

    UPDATE sharingList
	SET	   state_md =104, reject_msg = #{reject_msg}
	WHERE  brd_num = #{brd_num}
	AND    user_num = #{user_num}

mapper.select:
SELECT count(*)
FROM sharingList
WHERE user_num = #{user_num}

select user_num
from sharingList
where brd_num = #{brd_num}


DELETE FROM sharingList
WHERE brd_num = #{brd_num}
AND user_num = #{user_num}
